# -*- coding: utf-8 -*-import wximport wx.xrc############################################################################# Class MyPanel1###########################################################################class MyPanel1(wx.Panel):    def __init__( self, parent ):        wx.Panel.__init__ ( self, parent, id = wx.ID_ANY, pos = wx.DefaultPosition, size = wx.Size( 500,300 ), style = wx.TAB_TRAVERSAL )        self.testSizer = wx.BoxSizer( wx.VERTICAL )        txt1 = "Test"        st1 = wx.StaticText(self, label=txt1, style=wx.ALIGN_CENTRE)        self.testSizer.Add(st1, flag=wx.ALL, border=5)        self.SetSizer( self.testSizer )        self.Layout()    def __del__( self ):        pass